!function(){let e=document.querySelector(".header");window.onscroll=()=>{window.pageYOffset>50?e.classList.add("header_active"):e.classList.remove("header_active")}}();const burgerItem=document.querySelector(".burger"),menu=document.querySelector(".header__nav"),menuCloseItem=document.querySelector(".header__nav-close");burgerItem.addEventListener("click",()=>{menu.classList.add("header__nav_active")}),menuCloseItem.addEventListener("click",()=>{menu.classList.remove("header__nav_active")});const links=document.querySelectorAll(".js-scroll");links.forEach(e=>{e.addEventListener("click",function(){let e=this.getAttribute("href"),t=document.querySelector(e);menu.classList.remove("header__nav_active"),t.scrollIntoView({behavior:"smooth",block:"start"})})});